import React, { useState, useEffect } from "react";
import axios from "axios";

function EditUserInfo({ isLoggedIn }) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [newUsername, setNewUsername] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [showSaveConfirmation, setShowSaveConfirmation] = useState(false);
  const [showCancelConfirmation, setShowCancelConfirmation] = useState(false);

  useEffect(() => {
    // Récupérez les données de l'utilisateur depuis la base de données lors du chargement du composant
    const userId = localStorage.getItem("userId");
    if (userId) {
      axios
        .get(`/api/users/${userId}`)
        .then((response) => {
          setFirstName(response.data.firstName);
          setLastName(response.data.lastName);
          setNewUsername(response.data.username);
        })
        .catch((error) => {
          console.error(
            "Erreur lors de la récupération des données de l'utilisateur :",
            error
          );
        });
    }
  }, []);

  const handleSave = () => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      axios
        .put(`/api/users/${userId}`, {
          firstName,
          lastName,
          username: newUsername,
        })
        .then((response) => {
          setShowSaveConfirmation(true);
          setTimeout(() => {
            setShowSaveConfirmation(false);
            resetFields();
          }, 2000);
        })
        .catch((error) => {
          console.error(
            "Erreur lors de la sauvegarde des données de l'utilisateur :",
            error
          );
        });
    }
  };
  // retourne le username, le firstname et le lastname que l'on peut modifier
  // bouton "save" pour la sauvegarde des données avec message de confirmation en cas de réussite
  // bouton "cancel" pour l'annulation des champs remplis du formulaire
  return (
    <div>
      {isLoggedIn && (
        <div className="edit-user-info">
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="firstName">First Name</label>
            <input
              type="text"
              id="firstName"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="lastName">Last Name</label>
            <input
              type="text"
              id="lastName"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
            />
          </div>
          <div className="buttons">
            <button className="save-btn" onClick={handleSave}>
              Save
            </button>
            <button className="cancel-btn" onClick={handleCancel}>
              Cancel
            </button>
          </div>
          {showSaveConfirmation && (
            <div className="confirmation-message">
              Enregistrement de vos données avec succès !
            </div>
          )}
          {showCancelConfirmation && (
            <div className="confirmation-message">
              Annulation réussi des modifications effectuée
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default EditUserInfo;
