import React, { useState } from "react";
import EditUserInfo from "../../components/EditUserInfo/EditUserInfo";

function Header() {
  const [showEditForm, setShowEditForm] = useState(false);
  const [userInfo, setUserInfo] = useState({
    firstName: "Tony",
    lastName: "Jarvis",
    username: "tonyjarvis",
  });
  const [editableUserInfo, setEditableUserInfo] = useState({
    firstName: userInfo.firstName,
    lastName: userInfo.lastName,
    username: userInfo.username,
  });

  const handleEditButtonClick = () => {
    setEditableUserInfo({
      firstName: userInfo.firstName,
      lastName: userInfo.lastName,
      username: userInfo.username,
    });
    setShowEditForm(true);
  };

  const handleEditComplete = (updatedUserInfo) => {
    setUserInfo(updatedUserInfo);
    setShowEditForm(false);
    // Ici, vous pouvez envoyer les nouvelles informations de l'utilisateur au serveur
  };

  const handleInputChange = (field, value) => {
    setEditableUserInfo((prevUserInfo) => ({
      ...prevUserInfo,
      [field]: value,
    }));
  };

  return (
    <div className="header">
      <h1>
        Welcome back
        <br />
        {userInfo.firstName} {userInfo.lastName}
      </h1>
      <button className="edit-button" onClick={handleEditButtonClick}>
        Edit Name
      </button>
      {showEditForm && (
        <EditUserInfo
          isLoggedIn={true}
          username={editableUserInfo.username}
          originalUserInfo={editableUserInfo}
          onEditComplete={handleEditComplete}
          onInputChange={handleInputChange}
        />
      )}
    </div>
  );
}

export default Header;
