import React, { useState } from "react";
import MainNavUser from "../../components/navuser/navuser";
import Account from "../../components/account/account";
import Header from "../../components/headeruser/headeruser";
import EditUserInfo from "../../components/EditUserInfo/EditUserInfo";
import Footer from "../../components/footer/footer";
import "../../pages/user/user.css";

function UserPage() {
  const [showEditForm, setShowEditForm] = useState(false);
  const [userInfo, setUserInfo] = useState({
    firstName: "Tony",
    lastName: "Jarvis",
    username: "tonyjarvis",
  });

  const handleEditButtonClick = () => {
    setShowEditForm(true);
  };

  const handleEditComplete = (updatedUserInfo) => {
    setUserInfo(updatedUserInfo);
    setShowEditForm(false);
    // Ici, on envoie les nouvelles informations au serveur
  };

  return (
    <div>
      <MainNavUser />
      <main className="main bg-dark">
        <Header />
        {showEditForm && (
          <div className="edit-user-info-container">
            <h2>Edit user info</h2>
            <EditUserInfo
              isLoggedIn={true}
              username={userInfo.username}
              originalUserInfo={{
                firstName: userInfo.firstName,
                lastName: userInfo.lastName,
                username: userInfo.username,
              }}
              onEditComplete={handleEditComplete}
            />
          </div>
        )}
        <h2 className="sr-only">Accounts</h2>
        <Account
          title="Argent Bank Checking (x8349)"
          amount="$2,082.79"
          description="Available Balance"
        />
        <Account
          title="Argent Bank Savings (x6712)"
          amount="$10,928.42"
          description="Available Balance"
        />
        <Account
          title="Argent Bank Credit Card (x8349)"
          amount="$184.30"
          description="Current Balance"
        />
      </main>
      <Footer />
    </div>
  );
}

export default UserPage;

