import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserProfile } from "../../actions/authActions";
import "../EditUserInfo/EditUserinfo.css";

const EditUserInfo = ({ onClose }) => {
  const dispatch = useDispatch();
  const { userId, accessToken } = useSelector((state) => state.auth);
  const [username, setUsername] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleFirstNameChange = (event) => {
    setFirstName(event.target.value);
  };

  const handleLastNameChange = (event) => {
    setLastName(event.target.value);
  };

  const handleSave = () => {
    dispatch(fetchUserProfile({ userId, accessToken, username }));
    onClose();
  };

  const handleCancel = () => {
    setUsername("");
    setFirstName("");
    setLastName("");
    onClose();
  };

  return (
    <div className="edit-user-info">
      <div className="form-group">
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="firstName">First Name</label>
        <input
          type="text"
          id="firstName"
          value={firstName}
          onChange={handleFirstNameChange}
          disabled
        />
      </div>
      <div className="form-group">
        <label htmlFor="lastName">Last Name</label>
        <input
          type="text"
          id="lastName"
          value={lastName}
          onChange={handleLastNameChange}
          disabled
        />
      </div>
      <div className="buttons">
        <button className="save-btn" onClick={handleSave}>
          Save
        </button>
        <button className="cancel-btn" onClick={handleCancel}>
          Cancel
        </button>
      </div>
    </div>
  );
};

export default EditUserInfo;

