import React from "react";

import EditUserInfo from "../../components/EditUserInfo/EditUserInfo";

function MainNavUser() {
  const [isEditUserInfoVisible, setIsEditUserInfoVisible] = useState(false);

  const toggleEditUserInfo = () => {
    setIsEditUserInfoVisible(!isEditUserInfoVisible);
  };

  return (
    <nav className="main-nav-user">
      <a href="#" className="main-nav-logo">
        Argent Bank
      </a>
      <div className="main-nav-user-actions">
        <button className="main-nav-user-settings" onClick={toggleEditUserInfo}>
          <FontAwesomeIcon icon={faUserCog} />
        </button>
        {isEditUserInfoVisible && <EditUserInfo />}
        <button className="main-nav-user-logout">
          <FontAwesomeIcon icon={faSignOutAlt} />
        </button>
      </div>
    </nav>
  );
}

export default MainNavUser;
