import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserProfile } from "../../actions/userActions";
import { updateUsername } from "../../actions/authActions";
import "../EditUserInfo/EditUserinfo.css";

const EditUserInfo = () => {
  const dispatch = useDispatch();
  const { userProfile, status } = useSelector((state) => state);
  const [userName, setUsername] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    dispatch(fetchUserProfile());
  }, [dispatch]);

  useEffect(() => {
    if (userProfile) {
      setUsername(userProfile.userName);
      setFirstName(userProfile.firstName);
      setLastName(userProfile.lastName);
    }
  }, [userProfile]);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleSave = async () => {
    try {
      await updateUsername(userName);
      setMessage("Informations enregistrées avec succès !");
    } catch (error) {
      setMessage("Une erreur est survenue lors de l'enregistrement.");
    }
  };

  const handleCancel = () => {
    setUsername(userProfile?.userName || "");
    setMessage("Modifications annulées.");
  };

  return (
    <div>
      <label htmlFor="userName">Nom d'utilisateur :</label>
      <input
        type="text"
        id="userName"
        value={userName}
        onChange={handleUsernameChange}
      />
      <label htmlFor="firstName">Prénom :</label>
      <input type="text" id="firstName" value={firstName} readOnly />
      <label htmlFor="lastName">Nom :</label>
      <input type="text" id="lastName" value={lastName} readOnly />
      <button onClick={handleSave}>Enregistrer</button>
      <button onClick={handleCancel}>Annuler</button>
      {message && <div>{message}</div>}
    </div>
  );
};

export default EditUserInfo;
