import React, { useState } from "react";
import { useSelector } from "react-redux";
import "../EditUserInfo/EditUserinfo.css";

const EditUserInfo = () => {
  const { firstName, lastName, userName } = useSelector(
    (state) => state.userProfile.userProfile
  );

  const [username, setUsername] = useState(userName);
  const [localFirstName, setLocalFirstName] = useState(firstName);
  const [localLastName, setLocalLastName] = useState(lastName);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  // Les fonctions handleFirstNameChange et handleLastNameChange restent inchangées

  const handleSave = () => {
    // Logique de sauvegarde des données, y compris la mise à jour du userName
  };

  const handleCancel = () => {
    // Réinitialiser les données du formulaire si nécessaire
  };

  return (
    <div>
      <div className="edit-user-info">
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={handleUsernameChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            value={localFirstName}
            onChange={handleFirstNameChange}
            disabled
          />
        </div>
        <div className="form-group">
          <label htmlFor="lastName">Last Name</label>
          <input
            type="text"
            id="lastName"
            value={localLastName}
            onChange={handleLastNameChange}
            disabled
          />
        </div>
        <div className="buttons">
          <button className="save-btn" onClick={handleSave}>
            Save
          </button>
          <button className="cancel-btn" onClick={handleCancel}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditUserInfo;
