import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateUsername, fetchUserProfile } from "../../actions/authActions";
import "../EditUserInfo/EditUserinfo.css";

const EditUserInfo = () => {
  const dispatch = useDispatch();
  const { userProfile, status, accessToken } = useSelector(
    (state) => state.auth
  );
  const [userName, setUsername] = useState("");
  const [message, setMessage] = useState("");
  const [isFormVisible, setIsFormVisible] = useState(false);

  useEffect(() => {
    if (accessToken) {
      dispatch(fetchUserProfile(accessToken));
    }
  }, [dispatch, accessToken]);

  useEffect(() => {
    if (userProfile) {
      setUsername(userProfile.userName);
    }
  }, [userProfile]);

  const handleEditName = () => {
    setIsFormVisible(true);
    setUsername(userProfile?.userName || "");
  };

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleSave = async () => {
    try {
      await dispatch(updateUsername(accessToken, userName));
      setMessage("Informations enregistrées avec succès !");
      setIsFormVisible(false);
    } catch (error) {
      setMessage("Une erreur est survenue lors de l'enregistrement.");
    }
  };

  const handleCancel = () => {
    setUsername(userProfile.userName);
    setMessage("Modifications annulées.");
    setIsFormVisible(false);
  };

  return (
    <>
      {!isFormVisible && (
        <button onClick={handleEditName}>Modifier le nom d'utilisateur</button>
      )}
      {isFormVisible && (
        <form onSubmit={handleSave}>
          <div>
            <label htmlFor="firstName">First Name:</label>
            <input
              type="text"
              id="firstName"
              name="firstName"
              value={userProfile?.firstName || ""}
              disabled={status === "loading"}
            />
          </div>
          <div>
            <label htmlFor="lastName">Last Name:</label>
            <input
              type="text"
              id="lastName"
              name="lastName"
              value={userProfile?.lastName || ""}
              disabled={status === "loading"}
            />
          </div>
          <div>
            <label htmlFor="userName">UserName:</label>
            <input
              type="text"
              id="userName"
              name="userName"
              value={userName}
              onChange={handleUsernameChange}
              disabled={status === "loading"}
            />
          </div>
          <div className="buttons">
            <button className="save-btn" type="submit">
              Save
            </button>
            <button className="cancel-btn" onClick={handleCancel}>
              Cancel
            </button>
          </div>
          {message && <div>{message}</div>}
        </form>
      )}
    </>
  );
};

export default EditUserInfo;

