import { createSlice } from "@reduxjs/toolkit";
import { signIn, signOut } from "../actions/authActions";

// Définition de l'état initial du Slice
const initialState = {
  user: {
    username: null, // Ajout de la propriété username
    // Autres propriétés de l'utilisateur
  },
  loading: false,
  error: null,
};

// Création du Slice "auth" avec les reducers
const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      // Gestion de l'action signIn.pending
      .addCase(signIn.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      // Gestion de l'action signIn.fulfilled
      .addCase(signIn.fulfilled, (state, action) => {
        state.loading = false;
        state.user = action.payload;
      })
      // Gestion de l'action signIn.rejected
      .addCase(signIn.rejected, (state, action) => {
        state.loading = false;
        state.error = {
          status: action.payload.status,
          message: action.payload.message,
        };
      })
      // Gestion de l'action signOut.fulfilled
      .addCase(signOut.fulfilled, (state) => {
        state.user = null;
      });
  },
});

export default authSlice.reducer;

