import { createSlice } from "@reduxjs/toolkit";
import * as authActions from "../actions/authActions";

const initialState = {
  user: null,
  loading: false,
  error: null,
};

const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(authActions.signIn.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(authActions.signIn.fulfilled, (state, action) => {
        state.loading = false;
        state.user = action.payload;
      })
      .addCase(authActions.signIn.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      .addCase(authActions.signOut.fulfilled, (state) => {
        state.user = null;
      })
      .addCase(authActions.updateUserInfo.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(authActions.updateUserInfo.fulfilled, (state, action) => {
        state.loading = false;
        state.user = {
          ...state.user,
          ...action.payload,
        };
      })
      .addCase(authActions.updateUserInfo.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export default authSlice.reducer;
